Scripts.OnClick:
-- OnClick(self, button, down)
EkWaitList_WaitFrame_Button_OnClick(self, button);
PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnEnter:
-- OnEnter(self, motion)
if (EkWaitList_ShowWaitTime == 1) then
	EkWaitList_SetTooltip_Corner(self, EkWaitList_TEXT_Waitlist_Waited_Column_Tooltip1, EkWaitList_TEXT_Waitlist_Waited_Column_Tooltip2);
else
	EkWaitList_SetTooltip_Corner(self, EkWaitList_TEXT_Waitlist_Added_Column_Tooltip1, EkWaitList_TEXT_Waitlist_Added_Column_Tooltip2);
end

-- OnEnter(self, motion)
if (EkWaitList_ShowRank == 1) then
	EkWaitList_SetTooltip_Corner(self, EkWaitList_TEXT_Waitlist_Rank_Column_Tooltip1, EkWaitList_TEXT_Waitlist_Rank_Column_Tooltip2);
else
	EkWaitList_SetTooltip_Corner(self, EkWaitList_TEXT_Waitlist_Percent_Column_Tooltip1, EkWaitList_TEXT_Waitlist_Percent_Column_Tooltip2);
end
